import React, { useState, useEffect } from "react";
import { db } from "./firebase"; // Importa tu configuración de Firebase
import {
  collection,
  getDocs,
  addDoc,
  updateDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";

const Appointments = () => {
  const [appointments, setAppointments] = useState([]);
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [patient, setPatient] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [editingAppointment, setEditingAppointment] = useState(null);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    const querySnapshot = await getDocs(collection(db, "appointments"));
    const appointmentsList = [];
    querySnapshot.forEach((doc) => {
      appointmentsList.push({ id: doc.id, ...doc.data() });
    });
    setAppointments(appointmentsList);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editingAppointment) {
      handleUpdate(e);
    } else {
      try {
        const docRef = await addDoc(collection(db, "appointments"), {
          date: date,
          time: time,
          patient: patient,
        });
        console.log("Cita guardada con ID: ", docRef.id);
      } catch (e) {
        console.error("Error al guardar la cita: ", e);
      }
    }
    setDate("");
    setTime("");
    setPatient("");
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!editingAppointment) return;

    const appointmentRef = doc(db, "appointments", editingAppointment.id);
    try {
      await updateDoc(appointmentRef, {
        date: date,
        time: time,
        patient: patient,
      });
      setEditingAppointment(null);
      fetchAppointments(); // Vuelve a obtener citas después de actualizar
    } catch (error) {
      console.error("Error al actualizar la cita: ", error);
    }
  };

  const handleDelete = async (appointmentId) => {
    const appointmentRef = doc(db, "appointments", appointmentId);
    try {
      await deleteDoc(appointmentRef);
      fetchAppointments(); // Vuelve a obtener citas después de eliminar
    } catch (error) {
      console.error("Error al eliminar la cita: ", error);
    }
  };

  const filteredAppointments = appointments.filter((appointment) =>
    appointment.patient.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <input
        type="text"
        placeholder="Buscar por nombre del paciente"
        value={searchTerm}
        onChange={handleSearch}
      />
      <form onSubmit={handleSubmit}>
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
        />
        <input
          type="text"
          placeholder="Nombre del paciente"
          value={patient}
          onChange={(e) => setPatient(e.target.value)}
        />
        <button type="submit">
          {editingAppointment ? "Actualizar" : "Agregar"}
        </button>
      </form>
      <div>
        {filteredAppointments.map((appointment) => (
          <div key={appointment.id}>
            <p>{appointment.patient}</p>
            <p>{appointment.date}</p>
            <p>{appointment.time}</p>
            <button onClick={() => handleEdit(appointment)}>Editar</button>
            <button onClick={() => handleDelete(appointment.id)}>
              Eliminar
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Appointments;
