// PatientList.js
import { useState, useEffect } from "react";
import { db } from "../firebaseConfig"; // Ajuste de la ruta

function PatientList({ onSelectPatient }) {
  const [patients, setPatients] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    const unsubscribe = db.collection("appointments").onSnapshot((snapshot) => {
      const patientsData = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setPatients(patientsData);
    });
    return () => unsubscribe();
  }, []);

  const handleDelete = async (id) => {
    await db.collection("appointments").doc(id).delete();
  };

  const filteredPatients = patients.filter((patient) =>
    patient.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <input
        type="text"
        placeholder="Buscar paciente"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <ul>
        {filteredPatients.map((patient) => (
          <li key={patient.id}>
            {patient.name} - {patient.date}
            <button onClick={() => onSelectPatient(patient)}>Editar</button>
            <button onClick={() => handleDelete(patient.id)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default PatientList;
