// Calendar.js
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import { useState, useEffect } from "react";
import { db } from "../firebaseConfig"; // Ajuste de la ruta

function Calendar({ onSelectDate }) {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    // Obtén las citas desde Firebase
    const unsubscribe = db.collection("appointments").onSnapshot((snapshot) => {
      const appointmentsData = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setAppointments(appointmentsData);
    });
    return () => unsubscribe();
  }, []);

  return (
    <FullCalendar
      plugins={[dayGridPlugin, interactionPlugin]}
      initialView="dayGridMonth"
      events={appointments}
      dateClick={(info) => onSelectDate(info.date)}
    />
  );
}

export default Calendar;
