import React, { useState } from "react";
import { db } from "./firebase.js"; // Importa tu configuración de Firebase
import { collection, addDoc } from "firebase/firestore";

const AppointmentForm = ({ onAddAppointment }) => {
  const [patient, setPatient] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Guarda la cita en Firestore
    try {
      const docRef = await addDoc(collection(db, "appointments"), {
        patient,
        date,
        time,
      });
      console.log("Cita guardada con ID: ", docRef.id);
      onAddAppointment({ patient, date, time });
      setDate("");
      setTime("");
      setPatient("");
    } catch (e) {
      console.error("Error al guardar la cita: ", e);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        required
      />
      <input
        type="time"
        value={time}
        onChange={(e) => setTime(e.target.value)}
        required
      />
      <input
        type="text"
        value={patient}
        onChange={(e) => setPatient(e.target.value)}
        placeholder="Nombre del paciente"
        required
      />
      <button type="submit">Agregar Cita</button>
    </form>
  );
};

export default AppointmentForm;
