// PatientForm.js
import { useState } from "react";
import { db } from "./firebaseConfig";

function PatientForm({ selectedPatient, onSave, onCancel }) {
  const [name, setName] = useState(selectedPatient ? selectedPatient.name : "");
  const [date, setDate] = useState(selectedPatient ? selectedPatient.date : "");

  const handleSave = async () => {
    if (selectedPatient) {
      // Actualizar paciente existente
      await db.collection("appointments").doc(selectedPatient.id).update({
        name,
        date,
      });
    } else {
      // Agregar nuevo paciente
      await db.collection("appointments").add({ name, date });
    }
    onSave();
  };

  return (
    <div>
      <h2>{selectedPatient ? "Editar Paciente" : "Nuevo Paciente"}</h2>
      <label>
        Nombre:
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </label>
      <label>
        Fecha de Cita:
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
      </label>
      <button onClick={handleSave}>Guardar</button>
      <button onClick={onCancel}>Cancelar</button>
    </div>
  );
}

export default PatientForm;
